viewpoint.map:
function(doc) {
    if (doc.type !== "Topology"){
        if(doc.i_cell !== undefined && doc.j_cell !== undefined)
           emit([doc.i_cell, doc.j_cell], null) }}

spatial.gridpoint:
function(doc) {
    if(doc.type !== undefined || doc.aadt !== undefined) return null;
        if(doc.i_cell !== undefined && doc.j_cell !== undefined)
           emit({type: "Point", coordinates: [+doc.i_cell, +doc.j_cell]}, null) 
}
